<script>
/* eslint-disable */

export default {
  name: "TicTacToe",
  data() {
    return {
      AI: false,
      currentPlayer: "X",
      XWins: false,
      OWins: false,
      catsGame: false,
      move: 0,
      gameBoard: [
        [false, false, false],
        [false, false, false],
        [false, false, false]
      ],
      XBoard: [
        [false, false, false],
        [false, false, false],
        [false, false, false]
      ],
      OBoard: [
        [false, false, false],
        [false, false, false],
        [false, false, false]
      ]
    };
  },
  methods: {
    clickedCell(i, j) {
      // If the cell was already clicked, or a winner has been declared, simply return.
      if (this.gameBoard[i][j] || this.XWins || this.OWins) {
        return;
      }
      // Otherwise update that index to the array which tracks clicked cells
      this.gameBoard[i].splice(j, 1, true);
      //Update move count
      this.move += 1;
      // Next, update the O array or X array depending on whose turn it is.
      if (this.currentPlayer == "X") {
        this.XBoard[i].splice(j, 1, true);
        this.currentPlayer = "O";

      } else {
        this.OBoard[i].splice(j, 1, true);
        this.currentPlayer = "X";
      }
      // Checks for wins when a win is possible.
      if(this.move > 4){
        this.XWins = this.checkWin(this.XBoard);
        this.OWins = this.checkWin(this.OBoard);
        if((this.move == 9)&&(!this.OWins)&&(!this.XWins)){
          this.catsGame = true;
        }
      }
      if(this.currentPlayer == "O" && this.AI){
        this.AIMove()
      }
    },
    checkWin(playerArray){
      return((playerArray[0][0] && playerArray[0][1] && playerArray[0][2])|| //First row win
          (playerArray[1][0] && playerArray[1][1] && playerArray[1][2])|| //Second row win
          (playerArray[2][0] && playerArray[2][1] && playerArray[2][2])|| //Third row win
          (playerArray[0][0] && playerArray[1][0] && playerArray[2][0])|| //First col win   
          (playerArray[0][1] && playerArray[1][1] && playerArray[2][1])|| //Second col win
          (playerArray[0][2] && playerArray[1][2] && playerArray[2][2])|| //Third col win
          (playerArray[0][0] && playerArray[1][1] && playerArray[2][2])|| //Diagonal win 1 (x=-y)
          (playerArray[0][2] && playerArray[1][1] && playerArray[2][0])) //Diagonal win 2 (x=y)
    },
    reset(){
      this.currentPlayer = "X";
      this.XWins = false;
      this.OWins = false;
      this.catsGame = false;
      this.move = 0;
      this.gameBoard = [
        [false, false, false],
        [false, false, false],
        [false, false, false]
      ];
      this.XBoard = [
        [false, false, false],
        [false, false, false],
        [false, false, false]
      ];
      this.OBoard = [
        [false, false, false],
        [false, false, false],
        [false, false, false]
      ];
    },
    AIMove(){
      var selectedMove = [-1,-1];
      var loopCount = 0
      do{
        loopCount++
        selectedMove[0] = Math.floor(Math.random() * Math.floor(3));
        selectedMove[1] = Math.floor(Math.random() * Math.floor(3));
      }while(this.gameBoard[selectedMove[0]][selectedMove[1]] == true && loopCount < 15)
      // Play first available space if loop becomes too big.
      if(loopCount >= 20){
        for(var i = 0; i < 3; i++){
          for(var j = 0; j < 3; j++){
            if(!gameBoard[i][j]){
              this.clickedCell(i,j);
              return;
            }
          }
        }
      }

      this.clickedCell(selectedMove[0],selectedMove[1])
    }
  }
};
</script>
